defmodule Crc32cTest do
  use ExUnit.Case

  doctest Crc32c

  describe "calc/1" do
    test "calculates crc32c" do
      {:ok, source} =
        Base.decode64(
          ""
        )

      assert {:ok, 1_580_765_482} = Crc32c.calc(source)
    end

    test "fails to calculate crc32c for non-binary data" do
      assert {:error, :not_binary_data} = Crc32c.calc(%{})
    end
  end

  describe "calc!/1" do
    test "calculates crc32c" do
      {:ok, source} =
        Base.decode64(
          ""
        )

      assert 1_580_765_482 = Crc32c.calc!(source)
    end

    test "fails to calculate crc32c for non-binary data" do
      assert_raise ArgumentError, "argument error", fn ->
        Crc32c.calc!(%{})
      end
    end
  end
end
